//Last Asset Log Received 
let LastHeartbeatTime = Heartbeat| summarize LastHeartbeat_Time = arg_max(TimeGenerated, *) by ResourceId = tolower(ResourceId);
Heartbeat| where TimeGenerated > ago(365d)| summarize LastHeartbeat = datetime_diff("second",now(), max(TimeGenerated)) by ResourceId = tolower(ResourceId)
| where ResourceId <> ""| where ResourceId <> "None"
| join kind=inner (LastHeartbeatTime) on ResourceId| project ResourceId, LastHeartbeat, LastHeartbeat_Time| sort by LastHeartbeat desc

//SentinelHealth by SentinelResourceType
SentinelHealth
| distinct SentinelResourceType

//Most activity by ResourceProvider
AzureActivity 
| summarize count() by ResourceProvider, OperationNameValue

// Accessing Management CLI/Powershell Functions (Microsoft Entra ID)
let applications = dynamic(["Azure Active Directory PowerShell", "Microsoft Azure PowerShell", "Graph Explorer", "ACOM Azure Website"]);
SigninLogs 
| where AppDisplayName in (applications)
| summarize count() by AppDisplayName, Identity

//Network connections and related events initiated by processes running on the endpoint.
//DeviceNetworkInfo > DHCP: Lease, IP, MAC (Microsoft Sentinel) > 
DeviceNetworkInfo  
| summarize FirstObserved=arg_min(TimeGenerated, IPv4Dhcp, IPAddresses, MacAddress, MachineGroup, DnsAddresses) by DeviceName

// Linux OS
Syslog
| where SourceSystem == "Linux"

// Mac OS Syslog 
Syslog
| where SourceSystem contains "mac" or SourceSystem contains "osx"

// Network Security Groups (NSG) 
AzureDiagnostics
| where Category == "NetworkSecurityGroupEvent"

// Azure Firewall 
AzureDiagnostics
| where ResourceType == "AZUREFIREWALLS"

//Azure Kubernetes Service (AKS) 
AzureDiagnostics
| where Category == "kube-audit"

//Container Activity Logs (Azure Monitor)
AzureDiagnostics
| where ResourceProvider == "MICROSOFT.CONTAINERSERVICE"

// VPN Gateway Configuration Logs (VPN Gateway)
AzureDiagnostics
|where Category == 'GatewayDiagnosticLog'

// Tunnel State Change & Connections Logs (VPN Gateway) 
AzureDiagnostics
|where Category == 'TunnelDiagnosticLog'

// IKE Control Messages & Events (VPN Gateway) 
AzureDiagnostics
|where Category == 'IKEDiagnosticLog'

// SQL 
AzureDiagnostics 
| where Category contains "SQL"

// Azure DDoS 
AzureDiagnostics 
| where ResourceType == "PUBLICIPADDRESSES"

// Changes to Static Routes and BGP Events (VPN Gateway) 
AzureDiagnostics
|where Category == 'RouteDiagnosticLog'

// Security Appliances
CommonSecurityLog
| summarize count() by DeviceProduct, DeviceVendor, TenantId, DeviceVersion, DeviceEventClassID

// Configured Email Filtering Policies (Microsoft 365)
EmailEvents
| summarize count() by ConfidenceLevel, DetectionMethods, DeliveryAction, EmailActionPolicy

//Configured Spam Filtering Policies (Microsoft 365)
EmailEvents
| where ConfidenceLevel contains "spam" or DetectionMethods contains "spam" or DeliveryAction == "Junked"

//Unhealthy assets
SecurityRecommendation
| where RecommendationState contains 'unhealthy'

//ThreatIntelligence
ThreatIntelligenceIndicator 
| where SourceSystem in ("SecurityGraph", "Azure Sentinel", "Microsoft Sentinel")

//Software updates > Azure Automation Update Management > 
Update 
| project _ResourceId, Classification, Title, PublishedDate, KBID, RevisionNumber, Optional, RebootBehavior, Approved, ApprovalSource, ProductVersion, PackageRepository, Type, ResourceGroup, ResourceType, OSType, ProductArch, CVENumbers, UpdateState, Product, SourceSystem

